.btn-menu {
    cursor: pointer;
    width: 100px;
    height: 100px;
    background-color: transparent;
    border: none;
    display: flex;
    align-items: center;
    justify-content: center;
    outline: none;
  }

  .btn-menu:focus {
    border: 1px $main-line solid;
  }

  .btn-menu__label {
    border: 0;
    clip: rect(0, 0, 0, 0);
    height: 1px;
    margin: -1px;
    overflow: hidden;
    padding: 0;
    position: absolute;
    white-space: nowrap;
    width: 1px;
  }

  .btn-menu__burger {
    position: relative;
  }

.lineBurger {
  transition-timing-function: ease-in;
  display: inline-block;
  width: 50px;
  height: 12px;
  background-color: $main-grey;
  border: solid 2px $main-line;
  box-sizing: border-box;
  transition: 500ms;
}

.firstLine {
  transform: translateY(4px);
}

.thirdLine {
  transform: translateY(-4px);
}

.btn-menu_open .secondLine {
  transform: translateX(100px);
}

.btn-menu_open .firstLine {
  transform: translate(0, 19px) rotate(-45deg) ;
}

.btn-menu_open .thirdLine {
  transform: translate(0, -19px) rotate(45deg);
}

.thirdLine::after {
  content: '';
  display: block;
  height: 14px;
  width: 8px;
  transform: translate(19px, -3px);
  background-color: $main-grey;
  opacity: 0;
  transition: 200ms;
  transition-timing-function: ease-in;

}

.btn-menu_open .thirdLine::after {
  opacity: 1;
}

